import React, {useState, useEffect} from 'react';
import logo from './logo.png';
import logo2 from './logo512.png';
import './App.css';




function App() {

  const [load, setload ] = useState(false);

  useEffect(() => {
    setload(true)
    setTimeout(() => {
      setload(false)
    }, 1350);
  },[])
  return (
    <div className="App">
      {
        load ? 
        (
          <div className="App-header"> 
            <picture>
              <source media="(max-width: 799px)" srcset={logo2} width={"100%"} height={"100%"}/>
              <source media="(min-width: 800px)" srcset={logo} width={"75%"} height={"100%"}/>
              <img className="App-logo" src={logo} title="logo" />
            </picture>
          </div>
        ) :
      (<div>
        <header className="App-header">
        <h1>Capsule Corp.</h1>
      </header>
      <footer></footer>
      </div>
      )
      }
    </div>
  );
}

export default App;
